import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { StyleSheet, SafeAreaView } from 'react-native'
import { loadAllDecks } from '../redux/actions'
import Deck from '../components/Deck'
import { lightGreen } from '../utils/colors'
import { connect } from 'react-redux'

const DeckList = ({ navigation, dispatch }) => {
  useEffect(async () => {
    const action = await loadAllDecks(dispatch)
    console.log(action)
    dispatch(action)
  }, [dispatch])

  const decks = useSelector((decks) => decks)

  return (
    <SafeAreaView style={styles.container}>
      {Object.keys(decks).map((deck) => {
        const questions = decks[deck].questions
        return (
          <Deck
            key={deck}
            id={deck}
            questions={questions}
            onPress={() => navigation.navigate('Deck', { title: deck })}
          />
        )
      })}
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: lightGreen,
  },
})

export default connect()(DeckList)
